def new_plot_origin_map_2d(maze, agents):
    if agents is None:
        return
    h = len(maze)
    w = len(maze[0])

    marker_size = 12100  # default marker size
    map_size = max(h, w)
    if map_size <= 30:
        f = lambda x: 0.0129334 * x ** 5 - 1.37867 * x ** 4 + 53.8 * x ** 3 - 897.934 * x ** 2 + 4931 * x + 11280
        marker_size = f(map_size)
    elif map_size <= 50:
        f = lambda x: -0.01 * x ** 3 + 2.45 * x ** 2 - 185.5 * x + 4770
        marker_size = f(map_size)
    elif map_size <= 70:
        f = lambda x: -7 / 3000 * x ** 3 + 0.61 * x ** 2 - 56.8667 * x + 1980
        marker_size = f(map_size)
    else:
        f = lambda x: -1 / 750 * x ** 3 + 0.41 * x ** 2 - 1313 / 30 * x + 1700
        marker_size = f(map_size)
    marker_size /= 5

    color = ['b', 'g', 'r', 'c', 'm', 'y', 'k', 'tab:gray']
    color_idx = 0

    fig = plt.figure(figsize=(10, 13))
    for agent in agents:
        path = agent.path
        start_node = agent.start
        end_node = agent.goal

        plt.rc('font', size=(-(1 / 9) * h + 16))
        fix_x_idx = []
        fix_y_idx = []
        mov_x_idx = []
        mov_y_idx = []
        for i in range(h):
            for j in range(w):
                if maze[i][j] == 1 or maze[i][j] == 3 or maze[i][j] == 4:
                    fix_x_idx.append(i)
                    fix_y_idx.append(j)
                elif maze[i][j] == 2:
                    mov_x_idx.append(i)
                    mov_y_idx.append(j)
        plt.scatter(fix_y_idx, fix_x_idx, s=marker_size, marker='s', c='k')
        plt.scatter(mov_y_idx, mov_x_idx, s=marker_size, marker='s', c='k')
        if start_node is None and end_node is None:
            plt.scatter(0, 0, s=marker_size, marker='s')
            plt.text(0, 0, 'Start', verticalalignment='center', horizontalalignment='center')
            plt.scatter(h - 1, w - 1, s=marker_size, marker='s')
            plt.text(h - 1, w - 1, 'End', verticalalignment='center', horizontalalignment='center')
        else:
            plt.scatter(start_node[1], start_node[0], s=marker_size, marker='s')
            plt.text(start_node[1], start_node[0], 'Start', verticalalignment='center', horizontalalignment='center')
            plt.scatter(end_node[1], end_node[0], s=marker_size, marker='s')
            plt.text(end_node[1], end_node[0], 'End', verticalalignment='center', horizontalalignment='center')

        if path is not None:
            x = np.array(path).T[0]
            y = np.array(path).T[1]
            plt.plot(y, x, color[color_idx], linewidth=(-(1 / 30) * h + 3.7))
            color_idx = color_idx + 1 if color_idx + 1 <= 7 else 0

    plt.axis([-1, w, -1, h])
    plt.savefig("saved_image", dpi=200)
    plt.show()
    plt.pause(1)
    # plt.close()


def path_generation():
    path = []
    agents = []
    print(os.getcwd())
    filename = os.getcwd() + "/multiagentpath4.txt"
    f = open(filename, 'r')

    path_size = int(f.readline()) * 2
    if path_size == 0:
        return agents
    flag = False

    while True:
        path = []
        linelist = []
        for j in range(path_size):
            line = f.readline()
            line = line.strip()
            if not line:
                flag = True
                break
            if len(linelist) == 0:
                linelist.append(int(line))
            else:
                linelist.append(int(line))
                path.append(copy.deepcopy(linelist))
                linelist.clear()
        if flag:
            break
        agent = Agent(start=path[0], cur=path[0], goal=path[-1], path=copy.deepcopy(path))
        agents.append(agent)
        path.clear()

    f.close()
    return agents


if __name__ == '__main__':
    map = test_maze_gen_3()
    agents = path_generation()
    new_plot_origin_map_2d(map, agents)
